import { useState } from "react";
import Navbar from "./navbar";

export default function LoginPage() {
  return (
    <>
      <Navbar />
      <LoginA />
    </>
  );
}

function LoginA() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [curso, setCurso] = useState("");
  const LoginItens = { email, password, curso, id: Date.now() };
  console.log(LoginItens);
  return (
    <form className="container" style={{ marginTop: "30px" }}>
      <div class="mb-3">
        <label for="exampleInputEmail1" class="form-label">
          Email
        </label>
        <input
          type="email"
          class="form-control"
          id="exampleInputEmail1"
          aria-describedby="emailHelp"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
      </div>
      <div class="mb-3">
        <label for="exampleInputPassword1" class="form-label">
          Senha
        </label>
        <input
          type="password"
          class="form-control"
          id="exampleInputPassword1"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </div>
      <div>
        <select
          class="form-select"
          aria-label="Default select example"
          style={{ marginTop: "30px" }}
          value={curso}
          onChange={(e) => setCurso(e.target.value)}
        >
          <option selected>Selecionar Curso</option>
          <option value="1">JavaScript</option>
          <option value="2">Java</option>
          <option value="3">Python</option>
          <option value="4">HTML/CSS</option>
          <option value="5">React</option>
          <option value="6">MySQL</option>
          <option value="7">Flutter</option>
          <option value="8">Swift</option>
          <option value="9">PHP </option>
          <option value="10">C# </option>
        </select>
        <a href="/emailRecup">Esqueceu seu email</a>
      </div>
      {/* <div class="mb-3 form-check">
          <input type="checkbox" class="form-check-input" id="exampleCheck1" />
          <label class="form-check-label" for="exampleCheck1">
            Check me out
          </label>
        </div> */}
      <button type="submit" class="btn btn-primary" style={{ margin: "50px" }}>
        Entrar
      </button>
    </form>
  );
}
